import React, { useState } from 'react';
import './App.css';

function App() {
  // 1. 상태 관리 (기존 기능 유지)
  const [items, setItems] = useState([
    { id: 1, name: '강호동', score: 0 },
    { id: 2, name: '이수근', score: 0 }
  ]);
  const [inputValue, setInputValue] = useState('');

  // 2. 점수 조절 로직 (기능 그대로)
  const handleScore = (id, amount) => {
    setItems(items.map(item => 
      item.id === id ? { ...item, score: Math.max(0, item.score + amount) } : item
    ));
    // 감소 버튼 클릭 시 '땡!' 효과를 원한다면 여기에 로직 추가 가능
    if (amount < 0) console.log("땡!!!"); 
  };

  // 3. 항목 추가
  const addItem = () => {
    if (!inputValue.trim()) return;
    const newItem = {
      id: Date.now(),
      name: inputValue,
      score: 0
    };
    setItems([...items, newItem]);
    setInputValue('');
  };

  // 4. 항목 삭제
  const removeItem = (id) => {
    setItems(items.filter(item => item.id !== id));
  };

  return (
    <div className="app-container">
      <header className="main-header">
        <h1 className="title">신서유기 점수판</h1>
      </header>

      {/* 입력 영역 */}
      <div className="input-section score-item">
        <input 
          type="text" 
          value={inputValue} 
          onChange={(e) => setInputValue(e.target.value)}
          placeholder="이름 입력..."
          className="theme-input"
        />
        <button onClick={addItem} className="btn-add">추가</button>
      </div>

      {/* 리스트 영역 (73라인 에러 해결 지점) */}
      <div className="list-container">
        {items.map(item => (
          <div key={item.id} className="score-item animate-in">
            <div className="item-info">
              <span className="item-name">{item.name}</span>
              <button className="btn-delete" onClick={() => removeItem(item.id)}>x</button>
            </div>
            
            <div className="control-box">
              <button className="btn-minus" onClick={() => handleScore(item.id, -1)}>-</button>
              <span className="item-score">{item.score}</span>
              <button className="btn-plus" onClick={() => handleScore(item.id, 1)}>+</button>
            </div>
          </div>
        ))}
      </div>

      {items.length > 0 && (
        <button className="btn-reset" onClick={() => setItems([])}>모두 초기화</button>
      )}
    </div>
  );
}

export default App;
