import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { db } from '../firebase';
import { collection, doc, setDoc, updateDoc, onSnapshot, increment, deleteDoc, getDocs, query } from 'firebase/firestore';
import '../App.css'; // Ïä§ÌÉÄÏùº ÏûÑÌè¨Ìä∏

const ADMIN_PW = "1234"; 

const AdminPage = () => {
  const [isAuth, setIsAuth] = useState(sessionStorage.getItem("admin") === "true");
  const [pw, setPw] = useState("");
  const [teams, setTeams] = useState([]);
  const [count, setCount] = useState(0);
  const [steal, setSteal] = useState({ from: "", to: "", amount: 0 });

  useEffect(() => {
    return onSnapshot(collection(db, "teams"), (snap) => {
      setTeams(snap.docs.map(d => ({ id: d.id, ...d.data() })));
    });
  }, []);

  const login = () => {
    if (pw === ADMIN_PW) {
      setIsAuth(true);
      sessionStorage.setItem("admin", "true");
    } else alert("ÎπÑÎ∞ÄÎ≤àÌò∏Í∞Ä ÌãÄÎ†∏ÏäµÎãàÎã§.");
  };

  const createTeams = async () => {
  const teamNum = parseInt(count);
  if (isNaN(teamNum) || teamNum <= 0) return alert("Ïò¨Î∞îÎ•∏ ÌåÄ ÏàòÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî.");
  if (!window.confirm(`[Ï£ºÏùò] Î™®Îì† Í∏∞Ï°¥ Îç∞Ïù¥ÌÑ∞Î•º ÏÇ≠Ï†úÌïòÍ≥† ${teamNum}Í∞úÏùò ÌåÄÏúºÎ°ú Îã§Ïãú ÎßåÎì≠ÎãàÎã§.`)) return;

  try {
    // 1. ÌòÑÏû¨ FirestoreÏóê Ï†ÄÏû•Îêú 'Î™®Îì†' ÌåÄ Î¨∏ÏÑúÎ•º Í∞ÄÏ†∏Ïò§Í∏∞
    const q = query(collection(db, "teams"));
    const querySnapshot = await getDocs(q);

    // 2. Î™®Îì† Î¨∏ÏÑúÎ•º ÌïòÎÇòÏî© ÏÇ≠Ï†ú (Ïù¥Í≤å ÌôïÏã§Ìûà ÏßÄÏõåÏ£ºÎäî Î∂ÄÎ∂ÑÏûÖÎãàÎã§)
    const deletePromises = querySnapshot.docs.map((document) => 
      deleteDoc(doc(db, "teams", document.id))
    );
    await Promise.all(deletePromises);

    // 3. ÏÉàÎ°ú ÏöîÏ≤≠Ìïú ÏàòÎßåÌÅº ÌåÄ ÏÉùÏÑ±
    const createPromises = [];
    for (let i = 1; i <= teamNum; i++) {
      createPromises.push(
        setDoc(doc(db, "teams", `team${i}`), { 
          name: `${i}ÌåÄ`, 
          score: 0, 
          pendingRequest: null 
        })
      );
    }
    await Promise.all(createPromises);
    
    alert(`ÏôÑÏ†Ñ Ï¥àÍ∏∞Ìôî ÏôÑÎ£å! Ïù¥Ï†ú ${teamNum}Í∞ú ÌåÄÎßå Ï°¥Ïû¨Ìï©ÎãàÎã§.`);
  } catch (error) {
    console.error("Ï¥àÍ∏∞Ìôî Ï§ë Ïò§Î•ò Î∞úÏÉù:", error);
    alert("Í∂åÌïúÏù¥ ÏóÜÍ±∞ÎÇò ÎÑ§Ìä∏ÏõåÌÅ¨ Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
  }
};

  const addScore = async (id, val) => {
    const team = teams.find(t => t.id === id);
    // 0Ï†ê ÎØ∏Îßå Î∞©ÏßÄ Î°úÏßÅ
    const nextScore = Math.max(0, (team.score || 0) + val);
    await updateDoc(doc(db, "teams", id), { score: nextScore });
  };

  const runSteal = async () => {
    const { from, to, amount } = steal;
    const sAmt = Number(amount);
    if (!from || !to || sAmt <= 0) return alert("ÏûÖÎ†•Í∞íÏùÑ ÌôïÏù∏ÌïòÏÑ∏Ïöî.");
    if (from === to) return alert("ÏûêÍ∏∞ ÏûêÏã†ÏóêÍ≤åÏÑú Î∫èÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.");

    const fT = teams.find(t => t.id === from);
    const tT = teams.find(t => t.id === to);
    const actual = Math.min(fT.score, sAmt);

    await updateDoc(doc(db, "teams", from), { score: fT.score - actual });
    await updateDoc(doc(db, "teams", to), { score: tT.score + actual });
    alert("Í∞ïÏ†ú Ïù¥Îèô ÏôÑÎ£å!");
  };

  if (!isAuth) return (
    <div className="container" style={{marginTop: '100px'}}>
      <div className="card">
        <h2>üîí Í¥ÄÎ¶¨Ïûê Ïù∏Ï¶ù</h2>
        <input type="password" onChange={e => setPw(e.target.value)} placeholder="ÏïîÌò∏ ÏûÖÎ†•" />
        <button className="btn-primary" onClick={login}>Ï†ëÏÜçÌïòÍ∏∞</button>
      </div>
    </div>
  );

  return (
    <>
      <nav className="nav-header">
        <Link to="/" className="home-icon">üè† Ìôà</Link>
        <div style={{fontWeight:'bold'}}>Í¥ÄÎ¶¨Ïûê Î™®Îìú</div>
        <Link to="/leaderboard">üìä ÏàúÏúÑÌëú</Link>
      </nav>
      
      <div className="container">
        <h1>üõ† Í¥ÄÎ¶¨ ÏÑ§Ï†ï</h1>
        
        <div className="card">
          <h3>1. ÌåÄ Ï¥àÍ∏∞Ìôî</h3>
          <input type="number" onChange={e => setCount(e.target.value)} placeholder="ÏÉùÏÑ±Ìï† ÌåÄ Ïàò" />
          <button className="btn-primary" onClick={createTeams}>ÌåÄ ÏÉùÏÑ±/Ï¥àÍ∏∞Ìôî</button>
        </div>

        <div className="card">
          <h3>2. Í∞ïÏ†ú Ï†êÏàò Ïù¥Îèô</h3>
          <select onChange={e => setSteal({...steal, from: e.target.value})}>
            <option value="">Î∫èÍ∏∏ ÌåÄ ÏÑ†ÌÉù</option>
            {teams.map(t => <option key={t.id} value={t.id}>{t.name}</option>)}
          </select>
          <select onChange={e => setSteal({...steal, to: e.target.value})}>
            <option value="">Î∞õÏùÑ ÌåÄ ÏÑ†ÌÉù</option>
            {teams.filter(t => t.id !== steal.from).map(t => <option key={t.id} value={t.id}>{t.name}</option>)}
          </select>
          <input type="number" placeholder="Ïù¥ÎèôÌï† Ï†êÏàò" onChange={e => setSteal({...steal, amount: e.target.value})} />
          <button className="btn-success" onClick={runSteal}>Í∞ïÏ†ú Ïù¥Îèô Ïã§Ìñâ</button>
        </div>

        <h3>3. Ïã§ÏãúÍ∞Ñ ÌåÄ Î¶¨Ïä§Ìä∏</h3>
        <div className="admin-table-wrapper">
          <table>
            <thead>
              <tr><th>ÌåÄ</th><th>Ï†êÏàò</th><th>Ï°∞Ï†à</th><th>ÎßÅÌÅ¨</th></tr>
            </thead>
            <tbody>
              {teams.sort((a,b) => a.id.localeCompare(b.id, undefined, {numeric: true})).map(t => (
                <tr key={t.id}>
                  <td>{t.name}</td>
                  <td style={{fontWeight:'bold'}}>{t.score}</td>
                  <td>
                    <button onClick={() => addScore(t.id, 1)} style={{marginRight:'5px', padding:'5px 10px'}}>+1</button>
                    <button onClick={() => addScore(t.id, -1)} style={{padding:'5px 10px'}}>-1</button>
                  </td>
                  <td><Link to={`/team/${t.id.replace('team','')}`}>üîó</Link></td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </>
  );
};

export default AdminPage;
