import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { db } from '../firebase';
import { doc, onSnapshot, updateDoc, collection, deleteField, getDoc } from 'firebase/firestore';
import './App.css';

const TeamPage = () => {
  const { id } = useParams();
  const myTeamId = `team${id}`;
  const [myTeam, setMyTeam] = useState(null);
  const [allTeams, setAllTeams] = useState([]);
  const [targetTeamId, setTargetTeamId] = useState("");
  const [stealAmount, setStealAmount] = useState(0);

  useEffect(() => {
    // 1. ÎÇ¥ ÌåÄ Ï†ïÎ≥¥ Ïã§ÏãúÍ∞Ñ Íµ¨ÎèÖ
    const unsubMyTeam = onSnapshot(doc(db, "teams", myTeamId), (d) => {
      if (d.exists()) setMyTeam(d.data());
    });

    // 2. Ï†ÑÏ≤¥ ÌåÄ Î¶¨Ïä§Ìä∏ Íµ¨ÎèÖ (ÎåÄÏÉÅ ÏÑ†ÌÉùÏö©)
    const unsubAllTeams = onSnapshot(collection(db, "teams"), (s) => {
      setAllTeams(s.docs.map(d => ({ id: d.id, ...d.data() })));
    });

    return () => { unsubMyTeam(); unsubAllTeams(); };
  }, [myTeamId]);

  // [Í∏∞Îä• 1] ÏÉÅÎåÄÎ∞©ÏóêÍ≤å Ï†êÏàò Î∫èÍ∏∞ ÏöîÏ≤≠ Î≥¥ÎÇ¥Í∏∞
  const sendStealRequest = async () => {
    if (!targetTeamId || stealAmount <= 0) return alert("ÎåÄÏÉÅ ÌåÄÍ≥º Ï†êÏàòÎ•º ÌôïÏù∏ÌïòÏÑ∏Ïöî.");
    if (targetTeamId === myTeamId) return alert("ÏûêÍ∏∞ ÏûêÏã†ÏóêÍ≤åÎäî ÏöîÏ≤≠Ìï† Ïàò ÏóÜÏäµÎãàÎã§.");

    try {
      // ÏÉÅÎåÄÎ∞© ÌåÄ Î¨∏ÏÑúÏùò 'pendingRequest' ÌïÑÎìúÏóê ÎÇ¥ Ï†ïÎ≥¥Î•º Ï†ÅÏäµÎãàÎã§.
      const targetRef = doc(db, "teams", targetTeamId);
      await updateDoc(targetRef, {
        pendingRequest: {
          fromId: myTeamId,
          fromName: myTeam.name,
          amount: Number(stealAmount)
        }
      });
      alert(`${targetTeamId}Ïóê ÏöîÏ≤≠ÏùÑ Î≥¥ÎÉàÏäµÎãàÎã§. ÏÉÅÎåÄÍ∞Ä ÏäπÏù∏ÌïòÎ©¥ Ï†êÏàòÍ∞Ä Ïù¥ÎèôÌï©ÎãàÎã§!`);
    } catch (e) {
      alert("ÏöîÏ≤≠ Ï†ÑÏÜ° Ïã§Ìå®: " + e.message);
    }
  };

  // [Í∏∞Îä• 2] Îì§Ïñ¥Ïò® ÏöîÏ≤≠ ÏäπÏù∏ÌïòÍ∏∞ (ÎÇ¥ Ï†êÏàò ÍπéÏù¥Í≥† ÏÉÅÎåÄ Ï†êÏàò Ïò¨Î¶¨Í∏∞)
  const approveRequest = async () => {
  const request = myTeam.pendingRequest;
  if (!request) return;

  // 1. Ïã§Ï†úÎ°ú Ïù¥ÎèôÌï† Ï†êÏàò Í≥ÑÏÇ∞ 
  // ÎÇ¥ Ï†êÏàòÍ∞Ä ÏöîÏ≤≠ Ï†êÏàòÎ≥¥Îã§ Ï†ÅÏúºÎ©¥ ÎÇ¥ Ï†êÏàò Ï†ÑÎ∂ÄÎ•º, ÏïÑÎãàÎ©¥ ÏöîÏ≤≠ Ï†êÏàòÎßåÌÅº Ïù¥Îèô
  const actualAmount = Math.min(myTeam.score, request.amount);

  if (actualAmount <= 0) {
    alert("Î≥¥Ïú†Ìïú Ï†êÏàòÍ∞Ä ÏóÜÏñ¥ Î≥¥ÎÇº Ï†êÏàòÍ∞Ä ÏóÜÏäµÎãàÎã§!");
    await updateDoc(doc(db, "teams", myTeamId), { pendingRequest: deleteField() });
    return;
  }

  try {
    const attackerRef = doc(db, "teams", request.fromId);
    const myRef = doc(db, "teams", myTeamId);

    // 2. Í≥µÍ≤©Ïûê(ÏöîÏ≤≠Ïûê) Ï†êÏàò Ï¶ùÍ∞Ä (Ïã§Ï†ú Î∫èÏùÄ ÎßåÌÅºÎßå Ï¶ùÍ∞Ä)
    const attackerSnap = await getDoc(attackerRef);
    const currentAttackerScore = attackerSnap.data().score || 0;
    await updateDoc(attackerRef, { score: currentAttackerScore + actualAmount });

    // 3. ÎÇ¥(Î∞©Ïñ¥Ïûê) Ï†êÏàò Ï∞®Í∞ê Î∞è ÏöîÏ≤≠ ÏÇ≠Ï†ú (0Ï†ê ÎØ∏Îßå Î∞©ÏßÄ)
    await updateDoc(myRef, { 
      score: Math.max(0, myTeam.score - actualAmount), 
      pendingRequest: deleteField() 
    });

    alert(`ÏäπÏù∏ÎêòÏóàÏäµÎãàÎã§! ${actualAmount}Ï†êÏù¥ Ïù¥ÎèôÌñàÏäµÎãàÎã§.`);
  } catch (e) {
    alert("ÏäπÏù∏ Ï≤òÎ¶¨ Ï§ë Ïò§Î•ò: " + e.message);
  }
};

  // [Í∏∞Îä• 3] Îì§Ïñ¥Ïò® ÏöîÏ≤≠ Í±∞Ï†àÌïòÍ∏∞
  const rejectRequest = async () => {
    await updateDoc(doc(db, "teams", myTeamId), { pendingRequest: deleteField() });
    alert("ÏöîÏ≤≠ÏùÑ Í±∞Ï†àÌñàÏäµÎãàÎã§.");
  };

  if (!myTeam) return <div style={{padding:'20px'}}>ÌåÄ Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§Îäî Ï§ë... (ÌåÄ ID: {myTeamId})</div>;

  return (
    <div style={{ padding: '20px', maxWidth: '500px', margin: '0 auto', textAlign: 'center', fontFamily: 'sans-serif' }}>
      <header style={{ background: '#007bff', color: 'white', padding: '20px', borderRadius: '15px', marginBottom: '20px' }}>
        <h1 style={{ margin: 0 }}>{myTeam.name}</h1>
        <p style={{ fontSize: '2rem', fontWeight: 'bold', margin: '10px 0' }}>{myTeam.score} pts</p>
      </header>

      {/* Ï†êÏàò Î∫èÍ∏∞ ÏöîÏ≤≠ Ìèº */}
      <section style={{ border: '1px solid #ddd', padding: '20px', borderRadius: '10px', marginBottom: '20px' }}>
        <h3>üéØ ÌÉÄ ÌåÄ Ï†êÏàò Î∫èÍ∏∞ ÏöîÏ≤≠</h3>
        <select 
          onChange={e => setTargetTeamId(e.target.value)} 
          style={{ width: '100%', padding: '10px', marginBottom: '10px' }}
        >
          <option value="">ÎåÄÏÉÅ ÌåÄ ÏÑ†ÌÉù</option>
          {allTeams.filter(t => t.id !== myTeamId).map(t => (
            <option key={t.id} value={t.id}>{t.name} (ÌòÑÏû¨ {t.score}Ï†ê)</option>
          ))}
        </select>
        <input 
          type="number" 
          placeholder="Î∫èÏùÑ Ï†êÏàò" 
          onChange={e => setStealAmount(e.target.value)}
          style={{ width: '100%', padding: '10px', boxSizing: 'border-box', marginBottom: '10px' }}
        />
        <button onClick={sendStealRequest} style={{ width: '100%', padding: '10px', background: '#28a745', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer' }}>
          Î∫èÍ∏∞ ÏöîÏ≤≠ Î≥¥ÎÇ¥Í∏∞
        </button>
      </section>

      {/* ÎèÑÏ∞©Ìïú ÏöîÏ≤≠ ÏïåÎ¶º (ÏÉÅÎåÄÎ∞©Ïù¥ ÎÇòÏóêÍ≤å ÏöîÏ≤≠ÌñàÏùÑ ÎïåÎßå Î≥¥ÏûÑ) */}
      {myTeam.pendingRequest && (
        <section style={{ background: '#fff3cd', border: '2px solid #ffeeba', padding: '20px', borderRadius: '10px', animation: 'pulse 2s infinite' }}>
          <h3 style={{ color: '#856404', marginTop: 0 }}>‚ö†Ô∏è Î∫èÍ∏∞ ÏöîÏ≤≠ ÎèÑÏ∞©!</h3>
          <p><b>{myTeam.pendingRequest.fromName}</b>ÏóêÏÑú <b>{myTeam.pendingRequest.amount}Ï†ê</b>ÏùÑ Í∞ÄÏ†∏Í∞ÄÎ†§ Ìï©ÎãàÎã§.</p>
          <div style={{ display: 'flex', gap: '10px' }}>
            <button onClick={approveRequest} style={{ flex: 1, padding: '10px', background: '#dc3545', color: 'white', border: 'none', borderRadius: '5px', fontWeight: 'bold' }}>
              ÏäπÏù∏ (ÎÇ¥ Ï†êÏàò Ï∞®Í∞ê)
            </button>
            <button onClick={rejectRequest} style={{ flex: 1, padding: '10px', background: '#6c757d', color: 'white', border: 'none', borderRadius: '5px' }}>
              Í±∞Ï†à
            </button>
          </div>
        </section>
      )}

      <style>{`
        @keyframes pulse {
          0% { transform: scale(1); }
          50% { transform: scale(1.02); border-color: #ff0000; }
          100% { transform: scale(1); }
        }
      `}</style>
    </div>
  );
};

export default TeamPage;
