import React, { useState, useEffect } from 'react';
import { db } from '../firebase';
import { collection, onSnapshot } from 'firebase/firestore';
import '../App.css';

const Leaderboard = () => {
  const [teams, setTeams] = useState([]);

  useEffect(() => {
    return onSnapshot(collection(db, "teams"), (snap) => {
      setTeams(snap.docs.map(d => ({ id: d.id, ...d.data() })).sort((a,b) => b.score - a.score));
    });
  }, []);

  return (
    <div style={{backgroundColor: '#0f172a', minHeight: '100vh', padding: '40px 20px', color: 'white'}}>
      <div className="container">
        <h1 style={{textAlign: 'center', fontSize: '2.5rem', color: '#fbbf24', marginBottom: '40px'}}>üèÜ LIVE RANKING</h1>
        {teams.map((t, i) => (
          <div key={t.id} className="card" style={{
            backgroundColor: i === 0 ? '#1e293b' : '#1e293b',
            border: i === 0 ? '2px solid #fbbf24' : '1px solid #334155',
            display: 'flex', justifyContent: 'space-between', alignItems: 'center',
            padding: '20px', color: 'white'
          }}>
            <div style={{display:'flex', alignItems:'center'}}>
              <span style={{fontSize: '1.5rem', fontWeight: '800', marginRight: '20px', color: i < 3 ? '#fbbf24' : '#64748b'}}>
                {i + 1}
              </span>
              <span style={{fontSize: '1.2rem', fontWeight: '600'}}>{t.name}</span>
            </div>
            <div style={{fontSize: '1.8rem', fontWeight: '800'}}>{t.score} <span style={{fontSize: '1rem', fontWeight: '400'}}>pts</span></div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Leaderboard;
