import React, { useState, useEffect } from 'react';
import { db } from '../firebase';
import { collection, doc, setDoc, updateDoc, onSnapshot } from 'firebase/firestore';
import { Link } from 'react-router-dom';

const ADMIN_PW = "1234"; 

const AdminPage = () => {
  const [isAuth, setIsAuth] = useState(sessionStorage.getItem("admin") === "true");
  const [pw, setPw] = useState("");
  const [teams, setTeams] = useState([]);
  const [count, setCount] = useState(0);
  const [steal, setSteal] = useState({ from: "", to: "", amount: 0 });

  useEffect(() => {
    return onSnapshot(collection(db, "teams"), (snap) => {
      setTeams(snap.docs.map(d => ({ id: d.id, ...d.data() })));
    });
  }, []);

  const login = () => {
    if (pw === ADMIN_PW) {
      setIsAuth(true);
      sessionStorage.setItem("admin", "true");
    } else alert("ÎπÑÎ∞ÄÎ≤àÌò∏Í∞Ä ÌãÄÎ†∏ÏäµÎãàÎã§.");
  };

  const createTeams = async () => {
    if (count <= 0) return alert("ÌåÄ ÏàòÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî.");
    if (!window.confirm("Îç∞Ïù¥ÌÑ∞Î•º Ï¥àÍ∏∞ÌôîÌïòÏãúÍ≤†ÏäµÎãàÍπå?")) return;
    for (let i = 1; i <= count; i++) {
      await setDoc(doc(db, "teams", `team${i}`), { name: `${i}ÌåÄ`, score: 0, pendingRequest: null });
    }
    alert("ÏÉùÏÑ± ÏôÑÎ£å!");
  };

  const addScore = async (id, val) => {
    const team = teams.find(t => t.id === id);
    await updateDoc(doc(db, "teams", id), { score: (team.score || 0) + val });
  };

  const runSteal = async () => {
    if (!steal.from || !steal.to || steal.amount <= 0) return alert("Í∞íÏùÑ ÌôïÏù∏ÌïòÏÑ∏Ïöî.");
    const fTeam = teams.find(t => t.id === steal.from);
    const tTeam = teams.find(t => t.id === steal.to);
    await updateDoc(doc(db, "teams", steal.from), { score: fTeam.score - Number(steal.amount) });
    await updateDoc(doc(db, "teams", steal.to), { score: tTeam.score + Number(steal.amount) });
    alert("Ï†êÏàò Ïù¥Îèô ÏôÑÎ£å!");
  };

  if (!isAuth) return (
    <div style={{textAlign:'center', padding:'50px'}}>
      <h2>üîí Í¥ÄÎ¶¨Ïûê ÏïîÌò∏</h2>
      <input type="password" onChange={e => setPw(e.target.value)} />
      <button onClick={login}>Ï†ëÏÜç</button>
    </div>
  );

  return (
    <div style={{padding:'20px'}}>
      <h1>üõ† Í¥ÄÎ¶¨Ïûê Ìå®ÎÑê</h1>
      <section style={{border:'1px solid #ccc', padding:'10px', marginBottom:'20px'}}>
        <h3>1. ÌåÄ ÏÉùÏÑ±</h3>
        <input type="number" onChange={e => setCount(e.target.value)} placeholder="ÌåÄ Ïàò" />
        <button onClick={createTeams}>ÏÉùÏÑ±/Ï¥àÍ∏∞Ìôî</button>
      </section>

      <section style={{border:'1px solid #ccc', padding:'10px', marginBottom:'20px'}}>
        <h3>2. Í∞ïÏ†ú Ï†êÏàò Î∫èÍ∏∞</h3>
        <select onChange={e => setSteal({...steal, from: e.target.value})}><option value="">Î∫èÍ∏∏ ÌåÄ</option>{teams.map(t => <option key={t.id} value={t.id}>{t.name}</option>)}</select>
        <select onChange={e => setSteal({...steal, to: e.target.value})}><option value="">Î∞õÏùÑ ÌåÄ</option>{teams.map(t => <option key={t.id} value={t.id}>{t.name}</option>)}</select>
        <input type="number" onChange={e => setSteal({...steal, amount: e.target.value})} placeholder="Ï†êÏàò" style={{width:'60px'}} />
        <button onClick={runSteal}>Ïã§Ìñâ</button>
      </section>

      <h3>3. ÌåÄ ÌòÑÌô© (1Ï†ê Îã®ÏúÑ Ï°∞Ï†à)</h3>
      <table border="1" style={{width:'100%', borderCollapse:'collapse'}}>
        <thead><tr style={{background:'#eee'}}><th>ÌåÄÎ™Ö</th><th>Ï†êÏàò</th><th>Ï°∞Ï†à</th><th>ÎßÅÌÅ¨</th></tr></thead>
        <tbody>
          {teams.sort((a,b) => a.id.localeCompare(b.id, undefined, {numeric: true})).map(t => (
            <tr key={t.id}>
              <td style={{textAlign:'center'}}>{t.name}</td>
              <td style={{textAlign:'center', fontWeight:'bold'}}>{t.score}</td>
              <td style={{textAlign:'center'}}>
                <button onClick={() => addScore(t.id, 1)}>+1</button>
                <button onClick={() => addScore(t.id, -1)}>-1</button>
              </td>
              <td style={{textAlign:'center'}}><Link to={`/team/${t.id.replace('team','')}`} target="_blank">Ïó¥Í∏∞</Link></td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminPage;
