import React, { useState } from 'react';
import './App.css';

function App() {
  const [score, setScore] = useState(0);

  // 신서유기 효과음(땡! 등)을 넣고 싶다면 여기서 Audio 객체를 활용하세요.
  const playSound = (type) => {
    // const audio = new Audio(type === 'up' ? 'success.mp3' : 'fail.mp3');
    // audio.play();
  };

  return (
    <div className="container">
      <h1 style={{fontSize: '2rem', marginBottom: '0'}}>현재 점수</h1>
      
      {/* 점수 표시부 */}
      <div className="score-display">
        {score}
      </div>

      {/* 조작부 */}
      <div className="button-group">
        <button className="btn btn-up" onClick={() => setScore(score + 1)}>성공!</button>
        <button className="btn btn-down" onClick={() => {
          if(score > 0) setScore(score - 1);
          alert('땡!!!'); // 신서유기 전매특허
        }}>안돼~</button>
      </div>
      
      <button 
        style={{marginTop: '20px', background: 'none', border: 'none', color: '#555'}}
        onClick={() => setScore(0)}
      >
        처음부터 다시하기
      </button>
    </div>
  );
}

export default App;
