import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { db } from '../firebase';
import { doc, onSnapshot, updateDoc, collection, deleteField } from 'firebase/firestore';

const TeamPage = () => {
  const { id } = useParams();
  const [myTeam, setMyTeam] = useState(null);
  const [teams, setTeams] = useState([]);
  const [target, setTarget] = useState("");
  const [amount, setAmount] = useState(0);

  useEffect(() => {
    onSnapshot(doc(db, "teams", `team${id}`), (d) => setMyTeam(d.data()));
    onSnapshot(collection(db, "teams"), (s) => setTeams(s.docs.map(d => ({id: d.id, ...d.data()}))));
  }, [id]);

  const requestSteal = async () => {
    if (!target || amount <= 0) return alert("ÎåÄÏÉÅÏùÑ ÏÑ†ÌÉùÌïòÍ≥† Ï†êÏàòÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî.");
    await updateDoc(doc(db, "teams", target), {
      pendingRequest: { from: `team${id}`, amount: Number(amount) }
    });
    alert("ÏöîÏ≤≠ÏùÑ Î≥¥ÎÉàÏäµÎãàÎã§.");
  };

  const approve = async () => {
    const req = myTeam.pendingRequest;
    const attacker = teams.find(t => t.id === req.from);
    await updateDoc(doc(db, "teams", `team${id}`), { score: myTeam.score - req.amount, pendingRequest: deleteField() });
    await updateDoc(doc(db, "teams", req.from), { score: attacker.score + req.amount });
  };

  if (!myTeam) return <p>Î°úÎî© Ï§ë...</p>;

  return (
    <div style={{textAlign:'center', padding:'20px'}}>
      <h1>üö© {myTeam.name} ÎåÄÏãúÎ≥¥Îìú</h1>
      <h2 style={{fontSize:'3rem'}}>{myTeam.score} pts</h2>
      <hr />
      <h3>üéØ Ï†êÏàò Î∫èÍ∏∞ ÏöîÏ≤≠</h3>
      <select onChange={e => setTarget(e.target.value)}>
        <option value="">ÎåÄÏÉÅ ÏÑ†ÌÉù</option>
        {teams.filter(t => t.id !== `team${id}`).map(t => <option key={t.id} value={t.id}>{t.name}</option>)}
      </select>
      <input type="number" onChange={e => setAmount(e.target.value)} placeholder="Ï†êÏàò" style={{width:'60px'}} />
      <button onClick={requestSteal}>Î≥¥ÎÇ¥Í∏∞</button>

      {myTeam.pendingRequest && (
        <div style={{border:'3px solid red', padding:'20px', marginTop:'30px', background:'#fff0f0'}}>
          <p>‚ö†Ô∏è <b>{myTeam.pendingRequest.from}</b>ÌåÄÏù¥ {myTeam.pendingRequest.amount}Ï†êÏùÑ ÏöîÏ≤≠ÌñàÏäµÎãàÎã§!</p>
          <button onClick={approve} style={{padding:'10px 20px', fontSize:'1.2rem'}}>ÎèôÏùò (ÎÇ¥ Ï†êÏàò Ï∞®Í∞ê)</button>
        </div>
      )}
    </div>
  );
};

export default TeamPage;
