import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { db } from '../firebase';
import { doc, onSnapshot, updateDoc, collection, deleteField, getDoc } from 'firebase/firestore';
import '../App.css';

const TeamPage = () => {
  const { id } = useParams();
  const myTeamId = `team${id}`;
  const [myTeam, setMyTeam] = useState(null);
  const [allTeams, setAllTeams] = useState([]);
  const [targetId, setTargetId] = useState("");
  const [amt, setAmt] = useState(0);

  useEffect(() => {
    onSnapshot(doc(db, "teams", myTeamId), (d) => setMyTeam(d.data()));
    onSnapshot(collection(db, "teams"), (s) => setAllTeams(s.docs.map(d => ({id: d.id, ...d.data()}))));
  }, [myTeamId]);

  const sendReq = async () => {
    if (!targetId || amt <= 0) return alert("ÎåÄÏÉÅÍ≥º Ï†êÏàòÎ•º ÌôïÏù∏ÌïòÏÑ∏Ïöî.");
    await updateDoc(doc(db, "teams", targetId), {
      pendingRequest: { fromId: myTeamId, fromName: myTeam.name, amount: Number(amt) }
    });
    alert("ÏöîÏ≤≠ÏùÑ Î≥¥ÎÉàÏäµÎãàÎã§!");
  };

  const approve = async () => {
    const req = myTeam.pendingRequest;
    const actual = Math.min(myTeam.score, req.amount); // ÎÇ¥ Ï†êÏàòÎßåÌÅºÎßå Î∫èÍ∏∞ÎèÑÎ°ù ÏÑ§Ï†ï
    const attackerRef = doc(db, "teams", req.fromId);
    
    const attSnap = await getDoc(attackerRef);
    await updateDoc(attackerRef, { score: (attSnap.data().score || 0) + actual });
    await updateDoc(doc(db, "teams", myTeamId), { score: myTeam.score - actual, pendingRequest: deleteField() });
    alert(`${actual}Ï†êÏù¥ Ïù¥ÎèôÎêòÏóàÏäµÎãàÎã§!`);
  };

  if (!myTeam) return <div className="container">Î°úÎî© Ï§ë...</div>;

  return (
    <div className="container">
      <div className="card" style={{background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)', color: 'white'}}>
        <h1 style={{fontSize: '1rem', margin: 0}}>{myTeam.name}</h1>
        <div style={{fontSize: '4rem', fontWeight: '800'}}>{myTeam.score}</div>
        <p style={{margin: 0, opacity: 0.8}}>ÌòÑÏû¨ Î≥¥Ïú† Ï†êÏàò</p>
      </div>

      <div className="card">
        <h3>üéØ ÌÉÄ ÌåÄ Ï†êÏàò Î∫èÍ∏∞</h3>
        <select onChange={e => setTargetId(e.target.value)}>
          <option value="">ÎåÄÏÉÅ ÌåÄ ÏÑ†ÌÉù</option>
          {allTeams.filter(t => t.id !== myTeamId).map(t => (
            <option key={t.id} value={t.id}>{t.name} ({t.score}Ï†ê)</option>
          ))}
        </select>
        <input type="number" placeholder="Î∫èÏùÑ Ï†êÏàò ÏûÖÎ†•" onChange={e => setAmt(e.target.value)} />
        <button className="btn-success" onClick={sendReq}>Î∫èÍ∏∞ ÏöîÏ≤≠ Î≥¥ÎÇ¥Í∏∞</button>
      </div>

      {myTeam.pendingRequest && (
        <div className="alert-box">
          <h3 style={{margin: '0 0 10px 0'}}>‚ö†Ô∏è Î∫èÍ∏∞ ÏöîÏ≤≠!</h3>
          <p><b>{myTeam.pendingRequest.fromName}</b>ÌåÄÏù¥ <b>{myTeam.pendingRequest.amount}Ï†ê</b>ÏùÑ ÏöîÏ≤≠ÌñàÏäµÎãàÎã§.</p>
          <div style={{display:'flex', gap:'10px'}}>
            <button className="btn-danger" style={{flex: 1}} onClick={approve}>ÏäπÏù∏(Ï†êÏàòÏù¥Îèô)</button>
            <button className="btn-primary" style={{flex: 1, backgroundColor:'#999'}} onClick={async () => {
              await updateDoc(doc(db, "teams", myTeamId), { pendingRequest: deleteField() });
            }}>Í±∞Ï†à</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default TeamPage;
