import React, { useState, useEffect } from 'react';
import { db } from '../firebase';
import { collection, onSnapshot } from 'firebase/firestore';

const Leaderboard = () => {
  const [teams, setTeams] = useState([]);

  useEffect(() => {
    return onSnapshot(collection(db, "teams"), (snap) => {
      const data = snap.docs.map(d => ({ id: d.id, ...d.data() }));
      setTeams(data.sort((a, b) => b.score - a.score));
    });
  }, []);

  return (
    <div style={{padding:'40px', textAlign:'center', backgroundColor:'#111', color:'#fff', minHeight:'100vh'}}>
      <h1 style={{fontSize:'3rem', color:'gold'}}>üèÜ LIVE RANKING</h1>
      <div style={{maxWidth:'600px', margin:'0 auto'}}>
        {teams.map((t, i) => (
          <div key={t.id} style={{fontSize:'24px', margin:'15px', padding:'20px', background: i===0 ? '#d4af37' : '#333', borderRadius:'12px', display:'flex', justifyContent:'space-between'}}>
            <span>{i + 1}ÏúÑ {t.name}</span>
            <span style={{fontWeight:'bold'}}>{t.score} pts</span>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Leaderboard;
