import React from 'react';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import AdminPage from './components/AdminPage';
import TeamPage from './components/TeamPage';
import Leaderboard from './components/Leaderboard';
import useTeams from './hooks/useTeams'; // Ïã§ÏãúÍ∞Ñ ÌåÄ Î™©Î°ùÏùÑ Í∞ÄÏ†∏Ïò§Îäî ÌõÖ (ÏïÑÎûò ÏÑ§Î™Ö)
import './App.css';

function MainMenu() {
  const teams = useTeams(); // Ïã§ÏãúÍ∞ÑÏúºÎ°ú ÏÉùÏÑ±Îêú ÌåÄ Î™©Î°ùÏùÑ Í∞ÄÏ†∏Ïò¥

  return (
    <div className="container" style={{ textAlign: 'center', paddingBottom: '100px' }}>
      <header style={{ margin: '40px 0' }}>
        <h1 style={{ fontSize: '2.5rem', fontWeight: '800', color: '#1e293b' }}>
          üîÆ Ïã†ÏÑúÏú†Í∏∞ üîÆ
        </h1>
        <p style={{ color: '#64748b' }}>ÎìúÎûòÍ≥§ Î≥ºÏùò Ï£ºÏù∏ÏùÄ ÎàÑÍµ¨Ïù∏Í∞Ä?!</p>
      </header>

      {/* 1. Ï†ÑÏ≤¥ ÏàúÏúÑÌëú Î≤ÑÌäº (Í∞ÄÏû• ÌÅ¨Í≤å Î∞∞Ïπò) */}
      <Link to="/leaderboard" style={{ textDecoration: 'none' }}>
        <div className="card" style={{ 
          background: 'linear-gradient(135deg, #fbbf24 0%, #f59e0b 100%)', 
          color: 'white',
          padding: '30px'
        }}>
          <h2 style={{ margin: 0 }}>üìä Ï†ÑÏ≤¥ Ïã§ÏãúÍ∞Ñ ÏàúÏúÑÌëú</h2>
          <p style={{ margin: '5px 0 0 0', opacity: 0.9 }}>ÌòÑÏû¨ ÎìúÎûòÍ≥§ Î≥º ÌòÑÌô© Î≥¥Í∏∞</p>
        </div>
      </Link>

      <h3 style={{ margin: '40px 0 20px 0', textAlign: 'left', paddingLeft: '10px' }}>
        üö© Ïö∞Î¶¨ ÌåÄ ÌéòÏù¥ÏßÄ ÏûÖÏû•
      </h3>

      {/* 2. ÌåÄÎ≥Ñ ÌéòÏù¥ÏßÄ ÏûÖÏû• Î≤ÑÌäº (Í∑∏Î¶¨Îìú Î∞∞Ïπò) */}
      <div style={{ 
        display: 'grid', 
        gridTemplateColumns: '1fr 1fr', 
        gap: '15px',
        marginBottom: '60px'
      }}>
        {teams.sort((a,b) => a.id.localeCompare(b.id, undefined, {numeric: true})).map((t) => (
          <Link key={t.id} to={`/team/${t.id.replace('team','')}`} style={{ textDecoration: 'none' }}>
            <div className="card" style={{ padding: '20px', margin: 0 }}>
              <h4 style={{ margin: 0, color: '#334155' }}>{t.name}</h4>
              <small style={{ color: '#94a3b8' }}>ÏûÖÏû•ÌïòÍ∏∞ ‚Üó</small>
            </div>
          </Link>
        ))}
        {teams.length === 0 && <p style={{ gridColumn: '1 / 3' }}>Í¥ÄÎ¶¨ÏûêÍ∞Ä ÌåÄÏùÑ ÏÉùÏÑ±Ìï† ÎïåÍπåÏßÄ Í∏∞Îã§Î†§Ï£ºÏÑ∏Ïöî.</p>}
      </div>

      <hr style={{ border: '0', borderTop: '1px solid #e2e8f0', margin: '40px 0' }} />

      {/* 3. Í¥ÄÎ¶¨Ïûê ÌéòÏù¥ÏßÄ ÎßÅÌÅ¨ (Îß® ÏïÑÎûò Î∞∞Ïπò) */}
      <footer style={{ opacity: 0.5 }}>
        <Link to="/admin" style={{ color: '#64748b', fontSize: '0.9rem' }}>
          ‚öôÔ∏è Í¥ÄÎ¶¨Ïûê ÏãúÏä§ÌÖú Ï†ëÏÜç
        </Link>
      </footer>
    </div>
  );
}

// ÎùºÏö∞ÌåÖ ÏÑ§Ï†ï
export default function App() {
  return (
    <BrowserRouter basename={process.env.PUBLIC_URL}>
      <Routes>
        <Route path="/" element={<MainMenu />} />
        <Route path="/admin" element={<AdminPage />} />
        <Route path="/team/:id" element={<TeamPage />} />
        <Route path="/leaderboard" element={<Leaderboard />} />
      </Routes>
    </BrowserRouter>
  );
}
